static int yuvtogray(int y, int u, int v, int sign3)
{
    unsigned int gray = 0;
    int r, g, b;
    static long int ruv, guv, buv;

    if (sign3)
    {
        sign3 = 0;
        ruv = 1159 * (v - 128);
        guv = 380 * (u - 128) + 813 * (v - 128);
        buv = 2018 * (u - 128);
    }

    r = (1164 * (y - 16) + ruv) / 1000;
    g = (1164 * (y - 16) - guv) / 1000;
    b = (1164 * (y - 16) + buv) / 1000;

    if (r > 255)
        r = 255;
    if (g > 255)
        g = 255;
    if (b > 255)
        b = 255;
    if (r < 0)
        r = 0;
    if (g < 0)
        g = 0;
    if (b < 0)
        b = 0;

    gray =  (r*299 + g*587 + b*114 + 500) / 1000;//(r + g + b) / 3;
    return gray;
}

static void yuyv2gray(unsigned char *yuyv, unsigned char *gray)
{
    int y0, u, y1, v;
    int i, j = 0;
    for (i = 0; i < 640 * 480 * 2;)
    {
        y0 = yuyv[i++];
        u = yuyv[i++];
        y1 = yuyv[i++];
        v = yuyv[i++];
        gray[j++] = yuvtogray(y0, u, v, 1);
        gray[j++] = yuvtogray(y0, u, v, 0);
    }
}

int save2bmpgray(unsigned char *yuyv, char *outfile)
{
    int i;
    FILE *fp;
    unsigned char gray[640 * 480];
    yuyv2gray(yuyv, gray);
    fp = fopen(outfile, "wb");
    if (!fp)
    {
        printf("can not open %s\n", outfile);
        return 0;
    }
    fwrite(bmpgrayhead, sizeof(bmpgrayhead), 1, fp);
    for (i = 0; i < 480; i++)
    {
        fwrite(gray + 640 * (479 - i), 640, 1, fp);
    }

    fclose(fp);
    return 1;
}

